mapper.select:
select * FROM jsh_depotitem where 1=1
and name like '%${name}%'
and type=${type}
and remark like '%${remark}%'
and ifnull(delete_Flag,'0') !='1'
limit #{offset},#{rows}

select dep.id,dep.headerId,dep.materialid,dep.munit,dep.opernumber, dep.basicnumber,dep.unitprice,dep.taxunitprice,dep.allprice,dep.remark, dep.img,dep.incidentals,dep.depotid,dep.anotherdepotid,dep.taxrate, dep.taxmoney,dep.taxlastmoney,dep.otherfield1,dep.otherfield2,dep.otherfield3, dep.otherfield4,dep.otherfield5,dep.mtype from jsh_depotitem dep,jsh_material mat where 1=1
and dep.HeaderId = #{depotheadId}
and dep.materialid=mat.id
and mat.enableSerialNumber = #{enableSerialNumber}
and ifnull(dep.delete_Flag,'0') !='1' and ifnull(mat.delete_Flag,'0') !='1'

mapper.delete:
delete from jsh_depotitem
where 1=1
and HeaderId in
(

    #{depotheadId}

)

mapper.update:
update jsh_depotitem set delete_Flag='1' where 1=1 and HeaderId in (
#{depotheadId}
)

update jsh_depotitem
set delete_Flag='1'
where 1=1
and id in (

    #{id}

)

mapper.select:
select
from jsh_depotitem where 1=1 and DepotId in (
#{depotId}
) and ifnull(delete_Flag,'0') !='1'

select
from jsh_depotitem where 1=1 and materialId in (
#{materialId}
) and ifnull(delete_Flag,'0') !='1'

SELECT m. NAME MaterialName, m.Model MaterialModel, mc.`Name` categoryName, CONCAT( '(', m.Standard, ')', '(', m.Color, ')' ) AS MaterialOther, m.unit MaterialUnit, ifnull(m.safetystock,0) safetystock, IFNULL(intype.BasicInNumber ,0) BasicInNumber, IFNULL(outtype.BasicOutNumber ,0) BasicOutNumber, ( IFNULL(intype.BasicInNumber ,0) - IFNULL(outtype.BasicOutNumber ,0) ) BasicNumber, (IFNULL(intype.BasicInNumber ,0) - IFNULL(outtype.BasicOutNumber ,0) - ifnull(m.safetystock,0)) BasicLinjieNumber FROM jsh_material m LEFT JOIN jsh_materialcategory mc ON mc.Id = m.CategoryId LEFT JOIN ( SELECT di.MaterialId, ifnull(sum(BasicNumber), 0) AS BasicInNumber FROM jsh_depothead dh INNER JOIN jsh_depotitem di ON dh.id = di.HeaderId AND ifnull(di.delete_Flag, '0') != '1' WHERE dh.type = '入库'
and di.DepotId= ${pid}
AND ifnull(dh.delete_Flag, '0') != '1' group by di.MaterialId ) intype ON intype.MaterialId = m.id LEFT JOIN ( SELECT di.MaterialId, ifnull(sum(BasicNumber), 0) AS BasicOutNumber FROM jsh_depothead dh INNER JOIN jsh_depotitem di ON dh.id = di.HeaderId AND ifnull(di.delete_Flag, '0') != '1' WHERE dh.type = '出库' AND dh.SubType != '调拨'
and di.DepotId= ${pid}
AND ifnull(dh.delete_Flag, '0') != '1' group by di.MaterialId ) outtype ON outtype.MaterialId = m.id WHERE 1 = 1 AND ifnull(m.delete_Flag, '0') != '1' AND intype.BasicInNumber > 0 ORDER BY (IFNULL(intype.BasicInNumber ,0) - IFNULL(outtype.BasicOutNumber ,0) - ifnull(m.safetystock,0))
limit #{offset},#{rows}
