mapper.select:
select * FROM jsh_materialcategory where 1=1 and ifnull(status,'0') !='2'
and name like '%${name}%'
and parentId = ${parentId}
and Id !=1
limit #{offset},#{rows}

SELECT ,#{currentId} as currentId FROM jsh_materialcategory WHERE ParentId = #{id}
and id !=#{currentId}
and ifnull(status,'0') !='2' order by sort asc

SELECT ,#{currentId} as currentId FROM jsh_materialcategory WHERE ParentId = -1 and ifnull(status,'0') !='2'
and id !=#{currentId}
order by sort asc

