mapper.select:
select m.* FROM jsh_material m where 1=1
and m.name =#{name}
and ifnull(m.delete_Flag,'0') !='1'

select Id, CategoryId, name, Mfrs, Packing, SafetyStock, Model, Standard, Color, Unit, Remark, RetailPrice, LowPrice, PresetPriceOne, PresetPriceTwo, UnitId, FirstOutUnit, FirstInUnit, PriceStrategy, Enabled,enableSerialNumber FROM jsh_material where 1=1 and enabled ='1' and enableSerialNumber ='1'
and name like #{name}
and model like #{model}
and ifnull(delete_Flag,'0') !='1' order by id desc

mapper.update:
update jsh_material set delete_Flag='1' where 1=1 and ifnull(delete_Flag,'0') !='1' and id in (
#{id}
)

mapper.select:
select
from jsh_material where 1=1 and categoryId in (
#{categoryId}
) and ifnull(delete_Flag,'0') !='1'

select
from jsh_material where 1=1 and unitId in (
#{unitId}
) and ifnull(delete_Flag,'0') !='1'

select m.*,me.bar_code m_bar_code,u.uname unitName, mc.name categoryName FROM jsh_material m left join jsh_material_extend me on m.id=me.material_id and ifnull(me.delete_Flag,'0') !='1' left JOIN jsh_unit u on m.UnitId = u.id and ifnull(u.delete_Flag,'0') !='1' left JOIN jsh_materialcategory mc on m.CategoryId = mc.id and ifnull(mc.status,'0') !='2' where 1=1
and me.id = ${meId}
and ifnull(m.delete_Flag,'0') !='1'
