mapper.select:
SELECT ,#{currentId} as currentId FROM jsh_organization WHERE org_parent_no = #{org_no}
and id !=#{currentId}
and ifnull(org_stcd,'0') !='5' order by sort asc

SELECT ,#{currentId} as currentId FROM jsh_organization WHERE org_parent_no = -1
and id !=#{currentId}
and ifnull(org_stcd,'0') !='5' order by sort asc

mapper.update:
update jsh_organization
set update_Time=#{updateTime},updater=#{updater},org_stcd='5'
where id in (

    #{id}

)

mapper.select:
