mapper.select:
select user.id, user.username, user.loginame, user.position, user.email, user.phonenum, user.description, user.remark,user.isystem,org.id as orgaId,user.tenant_id,org.org_abr,rel.user_blng_orga_dspl_seq, rel.id as orgaUserRelId FROM jsh_user user left join jsh_orga_user_rel rel on user.id=rel.user_id and ifnull(rel.delete_flag,'0') !='1' left join jsh_organization org on rel.orga_id=org.id and ifnull(org.org_stcd,'0') !='5' where 1=1 and ifnull(user.status,'0') not in('1','2')
and user.username like #{userName}
and user.loginame like #{loginName}
order by user.id desc

select user.id, user.username, user.loginame, user.position, user.email, user.phonenum, user.description, user.remark,user.isystem FROM jsh_user user where 1=1 and ifnull(user.status,'0') not in('1','2')
and user.userName = #{userName}
and user.loginame = #{loginame}
order by user.id desc

